        <div className="content">
          <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/register" component={Register} />
            <Route path="/login" component={Login} />
            <Route path="/premium-content" component={PremiumContent} />
          </Switch>
        </div> */}
      </BrowserRouter>
    </div>
  );
}

export default App;
