import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { getUser } from "./service/AuthService"

const Friends = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(getUser());
    const [connections, setConnections] = useState([])
    const [sent, setSent] = useState([])
    const [received, setReceived] = useState([])
    useEffect(() => {
        if (!user) {
            navigate("/login")
        } else {
            let c = []
            let s = []
            let r = []
            let f = user.friendList;
            console.log(user);
            for (let i = 0; i < f?.length; i++) {
                if (f[i].isAccepted === true) c.append(f[i].name)
                else if (f[i].status === "received") r.append(f[i].name)
                else if (f[i].status === "sent") s.append(f[i].name)
            }
            setConnections(c)
            setReceived(r)
            setSent(s)
        }
    }, [user])
    // useEffect(() => {
    //     let c = []
    //     let s = []
    //     let r = []
    //     let f = user?.friendList;
    //     console.log(user.friendList);
    //     for (let i = 0; i < f?.length; i++) {
    //         if (f[i].isAccepted === true) c.append(f[i].name)
    //         else if (f[i].status === "received") r.append(f[i].name)
    //         else if (f[i].status === "sent") s.append(f[i].name)
    //     }
    //     setConnections(c)
    //     setReceived(r)
    //     setSent(s)
    // }, [])
    if (user)
        return (
            <div>
                <div>My Connections</div>
                <ul>
                    {connections.map((fr) => {
                        return <li key={fr.name}>{fr.name}</li>
                    })}
                </ul>
                <div>Sent</div>
                <ul>
                    {sent.map((fr) => {
                        return <li key={fr.name}>{fr.name}</li>
                    })}
                </ul>
                <div>Received</div>
                <ul>
                    {received.map((fr) => {
                        return <li key={fr.name}>{fr.name}</li>
                    })}
                </ul>
            </div>
        )
}

export default Friends
