import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUser, resetUserSession, getUsers } from './service/AuthService';
import axios from 'axios';

const editUserUrl = "https://lqjgcqa4lf.execute-api.us-east-1.amazonaws.com/prod/edituser"

const Home = () => {
    const navigate = useNavigate();
    const user = getUser();
    const users = getUsers();
    // console.log("Users:", users)
    const [searchUser, setUser] = useState('');
    const name = user !== 'undefined' && user ? user.name : '';

    useEffect(() => {
        if (!user) {
            navigate("/login")
        }
    }, [user])

    const logoutHandler = () => {
        resetUserSession();
        navigate('/login');
    }

    const submitHandler = (event) => {
        event.preventDefault();
        if (searchUser.trim() === '') {
            return;
        }
    }

    const handleClick = (receiver) => {
        const requestBody = {
            "sender": user.username,
            "receiver": receiver,
            "isAccepted": false
        }
        axios.post(editUserUrl, requestBody).then(response => {
            console.log(response);
            setMessage('Request sent succesfully');
        }).catch(error => {
            if (error.response.status === 401 || error.response.status === 403) {
                setMessage(error.response.data.message);
            } else {
                setMessage('Backend server error');
            }
        })
    }

    if (user) {
        return (
            <div>
                Hello {name}! You have been logged in! <br />

                <form onSubmit={submitHandler}>
                    Search Users: <input type="text" value={searchUser} onChange={event => setUser(event.target.value)} />
                    <input type="submit" value="Search" />
                </form>
                <ul>
                    {users.filter((u) => u.username !== user.username).map((receiver) => {
                        return <div key={receiver.username} style={{ display: "flex" }}><li>{receiver.username}</li><button onClick={() => handleClick(receiver.username)}>Add Friend</button></div>
                    })}
                </ul>
                <input type="button" value="Logout" onClick={logoutHandler} />
            </div>
        )
    }
}

export default Home;
