import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { getUser } from "./service/AuthService"

const Friends = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState(getUser());
    const [connections, setConnections] = useState([])
    const [sent, setSent] = useState([])
    const [received, setReceived] = useState([])
    useEffect(() => {
        if (!user) {
            navigate("/login")
        } else {
            let c = []
            let s = []
            let r = []
            let f = user.friendList;
            console.log(user);
            for (let i = 0; i < f?.length; i++) {
                if (f[i].request === "accepted") c.push(f[i].name)
                else if (f[i].status === "received") r.push(f[i].name)
                else if (f[i].status === "sent") s.push(f[i].name)
            }
            setConnections(c)
            setReceived(r)
            setSent(s)
        }
    }, [user])
    // useEffect(() => {
    //     let c = []
    //     let s = []
    //     let r = []
    //     let f = user?.friendList;
    //     console.log(user.friendList);
    //     for (let i = 0; i < f?.length; i++) {
    //         if (f[i].isAccepted === true) c.push(f[i].name)
    //         else if (f[i].status === "received") r.push(f[i].name)
    //         else if (f[i].status === "sent") s.push(f[i].name)
    //     }
    //     setConnections(c)
    //     setReceived(r)
    //     setSent(s)
    // }, [])
    if (user)
        return (
            <div>
                <div>My Connections</div>
                <ul>
                    {connections?.map((fr) => {
                        console.log(fr);
                        return <li key={fr}>{fr}</li>
                    })}
                </ul>
                <div>Sent</div>
                <ul>
                    {sent?.map((fr) => {
                        return <li key={fr}>{fr}</li>
                    })}
                </ul>
                <div>Received</div>
                <ul>
                    {received?.map((fr) => {
                        return <li key={fr} style={{ display: "flex" }}>
                            <div>{fr}</div>
                            <button>Accept</button>
                            <button>Reject</button>
                        </li>
                    })}
                </ul>
            </div>
        )
}

export default Friends
